import './Nav.scss';
import { useState } from 'react';
import { Link } from 'react-router-dom';

const Nav = ({ animateTransition }) => {
    const [contactClassName, setContactClassName] = useState('link');
    const [homeClassName, setHomeClassName] = useState('link-selected');
    const [aboutMeClassName, setAboutMeClassName] = useState('link');
    const changeContactClassName = () => {
        if (contactClassName == 'link') {
            animateTransition();
        }
        setHomeClassName('link');
        setAboutMeClassName('link');
        setContactClassName('link-selected');
    }
    const changeHomeClassName = () => {
        if (homeClassName == 'link') {
            animateTransition();
        }
        setHomeClassName('link-selected');
        setAboutMeClassName('link');
        setContactClassName('link');
    }
    const changeAboutMeClassName = () => {
        if (aboutMeClassName == 'link') {
            animateTransition();
        }
        setHomeClassName('link');
        setAboutMeClassName('link-selected');
        setContactClassName('link');
    }

  return (
    <div className='nav'>
        <div className='nav__links'>
            <Link className={contactClassName} to='/contact' onSelect={changeContactClassName}>
                <h5>Contact</h5>
            </Link>
            <Link className={homeClassName} to='/' onSelect={changeHomeClassName}>
                <h5>Home</h5>
            </Link>
            <Link className={aboutMeClassName} to='/aboutmeintro' onSelect={changeAboutMeClassName}>
                <h5>About Me</h5>
            </Link>
        </div>
    </div>
  )
}

export default Nav
